contracts/ProductTokenUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title ProductTokenUpgradeable
 * @dev Upgradeable implementation of an ERC1155 token for products with role-based access control
 */
contract ProductTokenUpgradeable is 
    Initializable, 
    ERC1155Upgradeable, 
    AccessControlUpgradeable,
    UUPSUpgradeable
{
    using Strings for uint256;

    // Role definition
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");

    // Mapping to track total supply per token ID
    mapping(uint256 => uint256) private _productSupply;

    // Array to track all product IDs
    uint256[] private _productIds;

    // Mapping to track if a product ID exists in the array
    mapping(uint256 => bool) private _productIdExists;

    // Base URI for metadata
    string private _baseUri;

    // Events
    event ProductMinted(address indexed to, uint256 indexed productId, uint256 amount);
    event BaseURIUpdated(string newUri);
    event ProductBurned(address indexed from, uint256 indexed productId, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializer replaces the constructor for upgradeable contracts
     * @param baseUri Initial base URI for token metadata
     */
    function initialize(string memory baseUri) public initializer {
        __ERC1155_init("");
        __AccessControl_init();
        __UUPSUpgradeable_init();

        _baseUri = baseUri;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);
    }

    /**
     * @dev Mints tokens for a specific product ID to an address
     * @param to The address that will receive the minted tokens
     * @param productId The ID of the product to mint
     * @param amount The amount of tokens to mint
     * @notice Only accounts with MINTER_ROLE can call this function
     * @notice If the product ID doesn't exist yet, it will be created
     */
    function mint(address to, uint256 productId, uint256 amount) external onlyRole(MINTER_ROLE) {
        require(productId > 0, "Invalid product ID");
        require(amount > 0, "Invalid amount");

        // Check if the product ID exists in the mapping
        if (!_productIdExists[productId]) {
            _productIds.push(productId);
            _productIdExists[productId] = true;
        }
        _productSupply[productId] += amount;
        _mint(to, productId, amount, "");
        emit ProductMinted(to, productId, amount);
    }

    /**
     * @dev Returns the URI for a given token ID
     * @param tokenId The ID of the token to query
     * @return The URI string for the token metadata
     * @notice URI is constructed by concatenating the base URI, the token ID, and '.json'
     */
    function uri(uint256 tokenId) public view virtual override returns (string memory) {
        return string(abi.encodePacked(_baseUri, tokenId.toString(), ".json"));
    }

    /**
     * @dev Updates the base URI for all token metadata
     * @param newUri New base URI to set
     * @notice Only accounts with DEFAULT_ADMIN_ROLE can call this function
     * @notice Empty strings are not allowed as URIs
     */
    function setBaseURI(string memory newUri) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(bytes(newUri).length > 0, "Invalid base URI");

        _baseUri = newUri;
        emit BaseURIUpdated(newUri);
    }

    /**
     * @dev Gets the current supply of tokens for a specific product ID
     * @param productId ID of the product to query
     * @return Current total supply of the product tokens
     * @notice Will revert if the product ID does not exist or is invalid
     */
    function productSupply(uint256 productId) public view returns (uint256) {
        require(productId > 0, "Invalid product ID");
        require(_productIdExists[productId], "Product ID does not exist");

        return _productSupply[productId];
    }

    /**
     * @dev Returns an array of all existing product IDs
     * @return Array of product IDs that have been created
     */
    function getProductIds() public view returns (uint256[] memory) {
        return _productIds;
    }

    /**
     * @dev Burns tokens from a specific address
     * @param from Address from which to burn tokens
     * @param productId ID of the product to burn
     * @param amount Amount of tokens to burn
     * @notice Only accounts with MINTER_ROLE can call this function
     * @notice Will revert if the product ID does not exist or amount exceeds supply
     * @notice Will revert if the target address has insufficient balance
     */
    function burn(address from, uint256 productId, uint256 amount) external onlyRole(MINTER_ROLE) {
        require(productId > 0, "Invalid product ID");
        require(amount > 0, "Invalid amount");

        // Check if the product ID exists in the mapping
        require(_productIdExists[productId], "Product ID does not exist");
        require(amount <= _productSupply[productId], "Burn amount exceeds supply");

        require(balanceOf(from, productId) >= amount, "Insufficient balance");

        // Update product supply first
        _productSupply[productId] -= amount;

        // Call the parent implementation to burn tokens
        super._burn(from, productId, amount);

        emit ProductBurned(from, productId, amount);
    }

    /**
     * @dev See {IERC165-supportsInterface}
     * @param interfaceId The interface identifier to check support for
     * @return bool True if the contract supports the interface
     * @notice This implementation supports both ERC1155 and AccessControl interfaces
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Function that should revert when msg.sender is not authorized to upgrade the contract
     * @param newImplementation Address of the new implementation
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(UPGRADER_ROLE) {}
}

contracts/USDCFundraiserFactoryUpgradeab.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "./USDCFundraiserUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";

// Interface for ProductToken minting/burning
interface IProductToken {
    function mint(address to, uint256 productId, uint256 amount) external;
    function burn(address from, uint256 productId, uint256 amount) external;
}

contract USDCFundraiserFactoryUpgradeable is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    using Clones for address;

    uint256 public constant BASIS_POINTS = 10000; // 100% = 10000 basis points
    address public usdcAddress;
    address public productTokenAddress;
    uint256 public defaultFeePercentage;
    address public feeWallet;

    // Implementation contract address for USDCFundraiser
    address public fundraiserImplementation;

    // Array to track all deployed fundraisers
    address[] public deployedFundraisers;
    mapping(address => bool) public isFundraiser;

    event FundraiserCreated(address indexed fundraiser, address indexed creator);
    event ImplementationUpdated(address indexed newImplementation);
    event ProductTokenUpdated(address indexed newProductToken);
    event ProductIdMapped(
        address indexed fundraiser, 
        uint256 indexed originalProductId, 
        uint256 indexed uniqueProductId
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _usdcAddress,
        address _productTokenAddress,
        uint256 _defaultFeePercentage,
        address _feeWallet,
        address _fundraiserImplementation,
        address _initialOwner
    ) public initializer {
        __Ownable_init(_initialOwner);
        __UUPSUpgradeable_init();

        require(_usdcAddress != address(0), "Invalid USDC address");
        require(_productTokenAddress != address(0), "Invalid product token address");
        require(_feeWallet != address(0), "Invalid fee wallet");
        require(_fundraiserImplementation != address(0), "Invalid implementation");
        require(_initialOwner != address(0), "Invalid owner");

        usdcAddress = _usdcAddress;
        productTokenAddress = _productTokenAddress;
        defaultFeePercentage = _defaultFeePercentage;
        feeWallet = _feeWallet;
        fundraiserImplementation = _fundraiserImplementation;
    }

    /**
    * @dev Create a new fundraiser campaign
    * @param beneficiaryWallet The wallet to receive raised funds
    * @param fundingType The funding type (0: All or Nothing, 1: Limitless)
    * @param minimumTarget The minimum target amount for the campaign
    * @param deadline The deadline for the campaign
    * @param products The array of products for the campaign
    */
    function createFundraiser(
        address beneficiaryWallet,
        uint256 fundingType,
        uint256 minimumTarget,
        uint256 deadline,
        ProductConfig[] memory products
    ) external returns (address) {
        require(beneficiaryWallet != address(0), "Invalid beneficiary");
        require(deadline > block.timestamp, "Invalid deadline");
        require(products.length > 0, "No products");

        address campaignAdmin = msg.sender;

        // Deploy minimal proxy clone of the implementation
        address fundraiserClone = fundraiserImplementation.clone();

        // Initialize the clone with factory address
        // Pass original product configs - fundraiser will generate unique IDs internally
        USDCFundraiserUpgradeable(fundraiserClone).initialize(
            usdcAddress,
            beneficiaryWallet,
            feeWallet,
            defaultFeePercentage,
            fundingType,
            minimumTarget,
            deadline,
            productTokenAddress,
            address(this),  // Pass factory address for minting/burning
            products,  // Pass original products, not pre-hashed
            campaignAdmin,
            owner() // Factory owner becomes the fundraiser owner
        );

        // Emit mapping events for frontend after initialization
        for (uint256 i = 0; i < products.length; i++) {
            // Get the unique product ID that was generated by the fundraiser
            uint256 uniqueProductId = USDCFundraiserUpgradeable(fundraiserClone).getUniqueProductId(products[i].productId);

            // Emit event to help frontend map original ID to unique ID
            emit ProductIdMapped(fundraiserClone, products[i].productId, uniqueProductId);
        }

        // Track the deployed fundraiser
        deployedFundraisers.push(fundraiserClone);
        isFundraiser[fundraiserClone] = true;

        emit FundraiserCreated(fundraiserClone, msg.sender);
        return fundraiserClone;
    }

    /**
     * @dev Allows authorized fundraisers to mint tokens through the factory
     * @param to Address to mint tokens to
     * @param productId Product ID to mint
     * @param amount Amount to mint
     * @notice Only fundraisers created by this factory can call this function
     * @notice Factory must have MINTER_ROLE on ProductToken
     */
    function mintForFundraiser(address givenProductTokenAddress, address to, uint256 productId, uint256 amount) external {
        require(isFundraiser[msg.sender], "Only fundraisers can mint");
        IProductToken(givenProductTokenAddress).mint(to, productId, amount);
    }

    /**
     * @dev Allows authorized fundraisers to burn tokens through the factory
     * @param from Address to burn tokens from
     * @param productId Product ID to burn
     * @param amount Amount to burn
     * @notice Only fundraisers created by this factory can call this function
     * @notice Factory must have MINTER_ROLE on ProductToken
     */
    function burnForFundraiser(address givenProductTokenAddress, address from, uint256 productId, uint256 amount) external {
        require(isFundraiser[msg.sender], "Only fundraisers can burn");
        IProductToken(givenProductTokenAddress).burn(from, productId, amount);
    }

    /**
    * @dev Change the default fee percentage
    * @param newFeePercentage The new fee percentage (basis points)
    */
    function changeDefaultFeePercentage(uint256 newFeePercentage) external onlyOwner {
        require(newFeePercentage < BASIS_POINTS, "Fee percentage must be less than 100%");
        defaultFeePercentage = newFeePercentage;
    }

    /**
    * @dev Change the fee wallet
    * @param newFeeWallet The new fee wallet address
    */
    function changeFeeWallet(address newFeeWallet) external onlyOwner {
        require(newFeeWallet != address(0), "Invalid fee wallet");
        feeWallet = newFeeWallet;
    }

    /**
    * @dev Update the fundraiser implementation
    * @param newImplementation The new implementation address
    */
    function updateFundraiserImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "Invalid implementation");
        fundraiserImplementation = newImplementation;
        emit ImplementationUpdated(newImplementation);
    }

    /**
     * @dev Updates the product token address
     * @param newProductToken The address of the new product token contract
     * @notice Only the owner can update the product token address
     * @notice This only affects new fundraiser deployments, not existing ones
     */
    function updateProductTokenAddress(address newProductToken) external onlyOwner {
        require(newProductToken != address(0), "Invalid product token address");
        productTokenAddress = newProductToken;
        emit ProductTokenUpdated(newProductToken);
    }

    /**
    * @dev Get the fee wallet
    * @return The fee wallet address
    */
    function getFeeWallet() external view returns (address) {
        return feeWallet;
    }

    /**
    * @dev Get the deployed fundraisers count
    * @return The number of deployed fundraisers
    */
    function getDeployedFundraisersCount() external view returns (uint256) {
        return deployedFundraisers.length;
    }

    /**
    * @dev Get a deployed fundraiser by index
    * @param index The index of the fundraiser to retrieve
    * @return The address of the deployed fundraiser
    */
    function getDeployedFundraiser(uint256 index) external view returns (address) {
        require(index < deployedFundraisers.length, "Index out of bounds");
        return deployedFundraisers[index];
    }

    /**
    * @dev Authorize an upgrade to a new implementation
    * @param newImplementation The new implementation address
    */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/USDCFundraiserUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@chainlink/contracts/src/v0.8/automation/AutomationCompatible.sol";
import "./ProductTokenUpgradeable.sol";

struct ProductConfig {
    uint256 productId;
    uint256 price;
    uint256 supplyLimit;  // 0 means unlimited supply
}

// Interface for factory minting/burning
interface IFactory {
    function mintForFundraiser(address productTokenAddress, address to, uint256 productId, uint256 amount) external;
    function burnForFundraiser(address productTokenAddress, address from, uint256 productId, uint256 amount) external;
}

contract USDCFundraiserUpgradeable is Initializable, OwnableUpgradeable, PausableUpgradeable, ReentrancyGuardUpgradeable {
    using SafeERC20 for IERC20;

    uint256 public constant BASIS_POINTS = 10000; // 100% = 10000 basis points
    IERC20 public usdc;
    ProductTokenUpgradeable public productToken;
    address public factory;  // Factory address for minting/burning
    address public beneficiaryWallet;
    address public feeWallet;
    uint256 public minimumTarget;
    uint256 public deadline;
    uint256 public feePercentage; // Stored as basis points (e.g., 250 = 2.5%)

    uint256 public totalRaised;
    bool public finalized;
    mapping(uint256 => uint256) public tokenDeposits; // tokenId => deposit amount
    uint256[] public productIds;
    mapping(uint256 => uint256) public productSoldCount;    // productId => current sold count

    uint256 public fundingType; // 0 = all or nothing, 1 = limitless, 2 = flexible
    mapping(uint256 => ProductConfig) public products;
    mapping(uint256 => uint256) public uniqueToOriginalProductId; // uniqueProductId => originalProductId
    address public campaignAdmin;

    modifier onlyAdminOrOwner() {
        require(msg.sender == owner() || msg.sender == campaignAdmin, "Not authorized");
        _;
    }

    struct RegistrationParams {
        string name;
        bytes encryptedEmail;
        address upkeepContract;
        uint32 gasLimit;
        address adminAddress;
        uint8 triggerType;
        bytes checkData;
        bytes triggerConfig;
        bytes offchainConfig;
        uint96 amount;
    }

    event Deposit(address indexed depositor, uint256 netAmount, uint256 feeAmount);
    event Finalized(bool success, uint256 totalRaised);
    event Refund(address indexed depositor, uint256 amount, uint256 productId, uint256 quantity);
    event ProductAdded(uint256 indexed productId, uint256 price, uint256 supplyLimit);
    event ProductUpdated(uint256 indexed productId, uint256 oldPrice, uint256 newPrice, uint256 oldSupplyLimit, uint256 newSupplyLimit);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _usdcAddress,
        address _beneficiaryWallet,
        address _feeWallet,
        uint256 _feePercentage,
        uint256 _fundingType,
        uint256 _minimumTarget,
        uint256 _deadline,
        address _productTokenAddress,
        address _factoryAddress,
        ProductConfig[] memory _products,
        address _campaignAdmin,
        address _owner
    ) public initializer {
        __Ownable_init(_owner);
        __Pausable_init();
        __ReentrancyGuard_init();

        require(_usdcAddress != address(0), "Invalid USDC address");
        require(_beneficiaryWallet != address(0), "Invalid beneficiary wallet");
        require(_feeWallet != address(0), "Invalid fee wallet");
        require(_productTokenAddress != address(0), "Invalid product token address");
        require(_factoryAddress != address(0), "Invalid factory address");
        require(_deadline > block.timestamp, "Invalid deadline");
        require(_products.length > 0, "No products provided");
        require(_campaignAdmin != address(0), "Invalid campaign admin");
        require(_owner != address(0), "Invalid owner");
        require(_feePercentage < BASIS_POINTS, "Fee percentage must be less than 100%");

        usdc = IERC20(_usdcAddress);
        beneficiaryWallet = _beneficiaryWallet;
        feeWallet = _feeWallet;
        feePercentage = _feePercentage;
        minimumTarget = _minimumTarget;
        deadline = _deadline;
        productToken = ProductTokenUpgradeable(_productTokenAddress);
        factory = _factoryAddress;
        campaignAdmin = _campaignAdmin;
        fundingType = _fundingType;

        for (uint256 i = 0; i < _products.length; i++) {
            require(_products[i].price > 0, "Invalid product price");
            require(_products[i].productId > 0, "Invalid product ID");

            // Generate unique product ID for this campaign
            uint256 uniqueProductId = uint256(keccak256(
                abi.encodePacked(address(this), _products[i].productId)
            ));

            // Store with unique ID
            products[uniqueProductId] = ProductConfig({
                productId: uniqueProductId,
                price: _products[i].price,
                supplyLimit: _products[i].supplyLimit
            });
            productIds.push(uniqueProductId);

            // Store reverse mapping
            uniqueToOriginalProductId[uniqueProductId] = _products[i].productId;
        }
    }

    /**
    * @dev Deposit USDC for a specific product
    * @param productId The original product ID (not the unique hashed one)
    * @param quantity The quantity of the product to deposit
    */
    function deposit(uint256 productId, uint256 quantity) external nonReentrant whenNotPaused {
        // Generate unique product ID for this campaign
        uint256 uniqueProductId = _generateUniqueProductId(productId);

        ProductConfig memory product = products[uniqueProductId];
        require(finalized == false, "Campaign is finalized");
        if (fundingType != 1) {
            require(block.timestamp < deadline, "Campaign has ended");
        }
        require(product.price > 0, "Invalid product");
        require(quantity > 0, "Invalid quantity");

        // Check supply limit
        if (product.supplyLimit > 0) {
            require(productSoldCount[uniqueProductId] + quantity <= product.supplyLimit, "Exceeds supply limit");
        }

        uint256 totalAmount = product.price * quantity;
        uint256 feeAmount = (totalAmount * feePercentage) / BASIS_POINTS;
        uint256 netAmount = totalAmount - feeAmount;

        // Transfer USDC from user to contract
        usdc.safeTransferFrom(msg.sender, address(this), totalAmount);

        // Mint NFT via factory (instead of calling productToken directly)
        // Only proceed with fees and tracking if mint succeeds
        try IFactory(factory).mintForFundraiser(address(productToken), msg.sender, uniqueProductId, quantity) {
            // Mint successful - now safe to update tracking and pay fees

            // Update counts and totals
            productSoldCount[uniqueProductId] += quantity;
            totalRaised += netAmount; // Only count net amount toward target

            // Pay fee wallet immediately after successful mint
            if (feeAmount > 0) {
                usdc.safeTransfer(feeWallet, feeAmount);
            }

            emit Deposit(msg.sender, netAmount, feeAmount);
        } catch Error(string memory reason) {
            // Mint failed - refund full amount to user (no fees deducted)
            usdc.safeTransfer(msg.sender, totalAmount);
            revert(string.concat("NFT mint failed: ", reason));
        } catch (bytes memory /*lowLevelData*/) {
            // Handle low-level errors - refund full amount
            usdc.safeTransfer(msg.sender, totalAmount);
            revert("NFT mint failed with low-level error");
        }
    }

    /**
    * @dev Finalize the campaign
    */
    function finalize() public nonReentrant whenNotPaused {
        require(!finalized, "Already finalized");

        if (fundingType == 0) {
            // All or Nothing
            if (totalRaised >= minimumTarget) {
                // Target met - can finalize immediately, no need to wait for deadline
                _executeFinalization(true);
            } else {
                // Target not met - must wait for deadline
                require(block.timestamp > deadline, "Deadline not reached");

                // target not met and deadline passed - can finalize
                _executeFinalization(false);

            }
        } else if (fundingType == 1) {
            // Limitless - only admin/owner can finalize anytime
            require(msg.sender == owner() || msg.sender == campaignAdmin, "Not authorized");
            _executeFinalization(true);
        } else if (fundingType == 2) {
            // Flexible
            if (totalRaised >= minimumTarget) {
                // Target met - can finalize immediately, no need to wait for deadline
                _executeFinalization(true);
            } else {
                // Target not met - must wait for deadline
                require(block.timestamp > deadline, "Deadline not reached");
                _executeFinalization(true);
            }
        }
    }

    function _executeFinalization(bool releaseFunds) private {
        finalized = true;

        if (releaseFunds) {
            // Fees already paid on each deposit, so just transfer remaining balance to beneficiary
            uint256 contractBalance = usdc.balanceOf(address(this));

            if (contractBalance > 0) {
                usdc.safeTransfer(beneficiaryWallet, contractBalance);
            }
        }

        emit Finalized(releaseFunds, totalRaised);
    }

    /**
    * @dev Claim refund for a specific product
    * @param productId The original product ID (not the unique hashed one)
    * @param quantity The quantity of the product to refund
    */
    function claimRefund(uint256 productId, uint256 quantity) external nonReentrant whenNotPaused {
        // Generate unique product ID for this campaign
        uint256 uniqueProductId = _generateUniqueProductId(productId);

        ProductConfig memory product = products[uniqueProductId];
        require(product.price > 0, "Invalid product");

        // Funding Type 0: All or Nothing
        if (fundingType == 0) {
            // If target met (100% or more), no refunds allowed
            require(totalRaised < minimumTarget, "Target met - refunds not available");
        } 
        // Funding Type 1: Limitless
        else if (fundingType == 1) {
            // If not finalized: refunds allowed
            // If finalized: refunds not allowed
            require(finalized == false, "Campaign finalized - refunds not available");
        } 
        // Funding Type 2: Flexible
        else if (fundingType == 2) {
            // Before deadline: refunds allowed
            // After deadline: refunds not allowed
            require(block.timestamp < deadline, "Deadline passed - refunds not available");
            require(totalRaised < minimumTarget, "Target met - refunds not available");
        } else {
            revert("Invalid funding type");
        }

        // Burn the NFTs to get refund (via factory)
        uint256 balance = productToken.balanceOf(msg.sender, uniqueProductId);
        require(balance >= quantity, "Insufficient NFT balance");

        IFactory(factory).burnForFundraiser(address(productToken), msg.sender, uniqueProductId, quantity);

        uint256 fee = (product.price * feePercentage) / BASIS_POINTS;
        uint256 refundAmount = (product.price - fee) * quantity;
        usdc.safeTransfer(msg.sender, refundAmount);

        productSoldCount[uniqueProductId] -= quantity;
        totalRaised -= refundAmount;

        emit Refund(msg.sender, refundAmount, uniqueProductId, quantity);
    }

    /**
    * @dev Add a new product to the campaign
    * @param product Product configuration with original product ID
    * @notice The product ID will be converted to a unique ID internally
    */
    function addProduct(ProductConfig memory product) external onlyAdminOrOwner {
        require(product.price > 0, "Invalid price");
        require(product.productId > 0, "Invalid product ID");

        // Generate unique product ID for this campaign
        uint256 uniqueProductId = _generateUniqueProductId(product.productId);

        require(products[uniqueProductId].price == 0, "Product already exists");

        // Store with unique ID
        products[uniqueProductId] = ProductConfig({
            productId: uniqueProductId,
            price: product.price,
            supplyLimit: product.supplyLimit
        });
        productIds.push(uniqueProductId);

        // Store reverse mapping
        uniqueToOriginalProductId[uniqueProductId] = product.productId;

        emit ProductAdded(uniqueProductId, product.price, product.supplyLimit);
    }

    /**
    * @dev Update an existing product
    * @param productId The original product ID (will be converted to unique ID)
    * @param newPrice New price for the product
    * @param newSupplyLimit New supply limit (0 for unlimited)
    */
    function updateProduct(uint256 productId, uint256 newPrice, uint256 newSupplyLimit) external onlyAdminOrOwner {
        // Generate unique product ID
        uint256 uniqueProductId = _generateUniqueProductId(productId);

        require(products[uniqueProductId].price > 0, "Product does not exist");
        require(newPrice > 0, "Invalid price");

        // Prevent price changes after any sales
        if (newPrice != products[uniqueProductId].price) {
            require(productSoldCount[uniqueProductId] == 0, "Cannot change price after sales");
        }
        // Prevent supply limit changes after any sales (unless new supply limit is set to 0 which means unlimited supply)
        if (newSupplyLimit > 0) {
            require(newSupplyLimit >= productSoldCount[uniqueProductId], "Supply limit cannot be less than sold count");
        }

        uint256 oldPrice = products[uniqueProductId].price;
        uint256 oldSupplyLimit = products[uniqueProductId].supplyLimit;

        products[uniqueProductId].price = newPrice;
        products[uniqueProductId].supplyLimit = newSupplyLimit;

        emit ProductUpdated(uniqueProductId, oldPrice, newPrice, oldSupplyLimit, newSupplyLimit);
    }

    /**
    * @dev Generates a unique product ID by hashing campaign address with original product ID
    * @param originalProductId The original product ID provided by the user
    * @return The unique product ID for this campaign
    */
    function _generateUniqueProductId(uint256 originalProductId) internal view returns (uint256) {
        return uint256(keccak256(
            abi.encodePacked(address(this), originalProductId)
        ));
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function getProductIds() external view returns (uint256[] memory) {
        return productIds;
    }

    /**
    * @dev Get product by original product ID
    * @param productId The original product ID (not the unique hashed one)
    * @return The product configuration
    */
    function getProduct(uint256 productId) external view returns (ProductConfig memory) {
        uint256 uniqueProductId = _generateUniqueProductId(productId);
        return products[uniqueProductId];
    }

    /**
    * @dev Get unique product ID from original product ID
    * @param productId The original product ID
    * @return The unique product ID for this campaign
    */
    function getUniqueProductId(uint256 productId) external view returns (uint256) {
        return _generateUniqueProductId(productId);
    }

    /**
    * @dev Get original product ID from unique product ID
    * @param uniqueProductId The unique product ID
    * @return The original product ID
    */
    function getOriginalProductId(uint256 uniqueProductId) external view returns (uint256) {
        require(uniqueToOriginalProductId[uniqueProductId] > 0, "Product does not exist");
        return uniqueToOriginalProductId[uniqueProductId];
    }
}

